mapper.delete:
delete from paly
where id = #{id,jdbcType=INTEGER}
  and play_id = #{playId,jdbcType=VARCHAR}

mapper.update:
update paly
set play_time = #{playTime,jdbcType=TIMESTAMP},
  lan_type = #{lanType,jdbcType=VARCHAR},
  price = #{price,jdbcType=DECIMAL},
  room_id = #{roomId,jdbcType=VARCHAR},
  room_name = #{roomName,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
  and play_id = #{playId,jdbcType=VARCHAR}

mapper.select:
select id, play_id, play_time, lan_type, price, room_id, room_name
from paly
where id = #{id,jdbcType=INTEGER}
  and play_id = #{playId,jdbcType=VARCHAR}
